def PatternCount(Text, Pattern):
    count = 0
    for i in range(len(Text)- len(Pattern) + 1):
        if Text[i:i+len(Pattern)] == Pattern:
            count = count+1

    return count

Text = ""


count_1 = PatternCount(Text, "ATGATCAAG")
count_2 = PatternCount(Text, "CTTGATCAT")

sum = count_1 + count_2

print(sum)